class Main(commands.Cog):

    def __init__(self, bot):
        self.bot = bot

    @commands.Cog.listener()
    async def on_dsl_vote(self, data):
        if data['type'] == 'upvote':
            till = round(time() + (12 * 60 * 60))
            await Temp.create(user_id=int(data['user']), event_exp=10, till=till)
            player: Player = PlayerRoster().get(int(data["user"]))
            async with player:
                player.add_energy(15)

            log_event(data['user'], "vote", f"Voted")

        elif data['type'] == 'test':
            log_event(data['user'], "vote", "TESTTTTTTTTTTTTT")

    @commands.command()
    @commands.default_member_permissions(manage_guild=True)
    async def reset_database(self, ctx):
        await AllItems.all().delete()
        await AllBeasts.all().delete()
        await AllPets.all().delete()
        await insert_items()
        await ctx.send("Database reset!")
    
    @commands.command()
    @commands.default_member_permissions(manage_guild=True)
    async def clear_matches(self, ctx):
        await PvpMatches.all().delete()
        await ctx.send("PvpMatches cleared!")


